INSERT INTO `item` (`ino`, `iname`, `iprice`, `isum`, `gno`, `ico`, `iimg`) VALUES
(1, 'チョコボール<ピーナッツ>', 72, 100, 9, '\r\n・内容量：25g\r\n\r\n・栄養成分[ 1箱（25g）当り ]\r\n熱量：142kcal、たんぱく質：3.0g、脂質：9.2g、炭水化物：11.7g、ナトリウム：13mg、カルシウム：27mg\r\n\r\n・原材料\r\nピーナッツ、砂糖、植物油脂、カカオマス、小麦粉、とうもろこしでん粉、全粉乳、ココアバター、乳糖、水あめ、モルトエキス、ミルクカルシウム、デキストリン、食塩、光沢剤、香料、乳化剤（大豆由来）\r\n\r\n・アレルギー\r\n乳、小麦、ピーナッツ、大豆\r\n\r\n・説明\r\nピーナッツがこうばしいクエッ！！\r\nみんなであそべるキョロちゃんズ缶\r\nいっしょにおでかけ♪しようクエッ！\r\n', '1.jpg'),
(2, 'チョコボール<キャラメル>', 60, 100, 9, '\r\n・内容量：29g\r\n\r\n・栄養成分[ 1箱（29g）当り ]\r\n熱量：140kcal、たんぱく質：1.2g、脂質：6.0g、炭水化物：20.3g、ナトリウム：19mg、カルシウム：42mg\r\n\r\n・原材料\r\n砂糖、水あめ、植物油脂、カカオマス、加糖練乳、全粉乳、加糖脱脂練乳、ココアバター、乳糖、ホエイパウダー、小麦たんぱく、ミルクカルシウム、デキストリン、モルトエキス、食塩、ソルビトール、光沢剤、乳化剤（大豆由来）、増粘剤（カラギナン）、香料\r\n\r\n・アレルギー\r\n乳、小麦、大豆\r\n\r\n・説明\r\nモグモグおいしいチョコとキャラメル\r\nおもちゃのカンヅメは\r\nみんなであそべるキョロちゃんズ缶\r\nいっしょにおでかけ♪しようクエッ！', '3.jpg'),
(3, 'チョコボール<いちご>', 72, 100, 9, '\r\n・内容量：26g\r\n\r\n・栄養成分[ 1箱（26g）当り ]\r\n熱量：145kcal、たんぱく質：0.9g、脂質：8.7g、炭水化物：15.8g、ナトリウム：15m、カルシウム：34mg\r\n\r\n・原材料\r\n砂糖、植物油脂、全粉乳、ココアバター、デキストリン、米パフ（小麦を含む）、乳糖、いちごパウダー、水あめ、乳化剤（大豆由来）、光沢剤、香料、膨脹剤、ベニバナ赤色素\r\n\r\n・アレルギー\r\n乳、小麦、大豆\r\n\r\n・説明\r\nいちごチョコ＆サクサクのパフクエッ！\r\nみんなであそべるキョロちゃんズ缶\r\nいっしょにおでかけ♪しようクエッ！', '2.jpg'),
(6, 'ダース<ミルク>', 148, 100, 9, '\r\n・内容量：45g（12粒）\r\n\r\n・栄養成分[ 1粒（標準3.8g）当り ]\r\n熱量：22kcal、たんぱく質：0.3g、脂質：1.5g、炭水化物：1.9g、ナトリウム：2.8mg\r\n\r\n・原材料\r\n砂糖、ココアバター、全粉乳、カカオマス、植物油脂、脱脂粉乳、生クリーム、ヘーゼルナッツペースト、ホエイパウダー、乳化剤（大豆由来）、香料\r\n\r\n・アレルギー\r\n乳、大豆\r\n\r\n・説明\r\n1993年にダース＜ミルク＞＜ビター＞が発売されました。', '4.jpg'),
(7, 'ダース<ビター>', 158, 100, 9, '\r\n・内容量：45g（12粒）\r\n\r\n・栄養成分[ 1粒（標準3.8g）当り ]\r\n熱量：22kcal、たんぱく質：0.2g、脂質：1.5g、炭水化物：2.0g、ナトリウム：0～1mg\r\n\r\n・原材料\r\n砂糖、カカオマス、ココアバター、バターオイル、植物油脂、乳化剤(大豆由来)、香料\r\n\r\n・アレルギー\r\n乳、大豆\r\n\r\n・説明\r\n1993年にダース＜ミルク＞＜ビター＞が発売されました。', '5.jpg'),
(8, '白いダース', 158, 100, 9, '\r\n・内容量：45g（12粒）\r\n\r\n・栄養成分[ 1粒（標準3.8g）当り ]\r\n熱量：22kcal、たんぱく質：0.3g、脂質：1.5g、炭水化物：1.9g、ナトリウム：3.2mg\r\n\r\n・原材料\r\n砂糖、全粉乳、ココアバター、植物油脂、脱脂粉乳、乳化剤（大豆由来）、香料\r\n\r\n・アレルギー\r\n乳、大豆\r\n\r\n・説明\r\n1993年にダース＜ミルク＞・＜ビター＞が発売されました。', '6.jpg'),
(9, 'ダース<ミント>', 158, 100, 9, '\r\n・内容量：12粒\r\n\r\n・栄養成分[ 1粒（標準3.8g）当り ]\r\n熱量：23kcal、たんぱく質：0.18g、脂質：1.5g、炭水化物：2.1g、ナトリウム：1.4mg\r\n\r\n・原材料\r\n砂糖、カカオマス、ココアバター、植物油脂、乳糖、全粉乳、デキストリン、脱脂粉乳、濃縮ホエイ、バターオイル、ホエイパウダー、ミント風味チップ、ペパーミントリーフ粉末、乳化剤（大豆由来）、香料、ベニバナ黄色素、加工デンプン、クチナシ青色素、増粘多糖類\r\n\r\n・アレルギー\r\n乳、大豆\r\n\r\n・説明\r\n今年も夏限定＜ミント＞が登場！\r\n口に広がるミントの爽快感。\r\nチップのカリッとした食感のアクセントがクセになるダース＜ミント＞です。', '7.jpg'),
(10, 'チョコでくるんだザクザククッキー', 198, 100, 10, '\r\n・内容量：1個\r\n\r\n・栄養成分[ 1個（標準34g）当り ]\r\n熱量：180kcal、たんぱく質：1.9g、脂質：9.9g、炭水化物：20.7g、ナトリウム：50mg\r\n\r\n・原材料\r\n砂糖、小麦粉、フルーツゼリー（濃縮アプリコットピューレ、濃縮りんごピューレ、転化糖、砂糖）、ショートニング、カカオマス、全粉乳、ココアバター、植物油脂、チョコレートチップ、アーモンド、脱脂粉乳、ココアパウダー、乳糖、食塩、果糖、グリセリン、香料、セルロース、膨脹剤、乳化剤（大豆由来）、酸味料、ゲル化剤（ペクチン）、酸化防止剤（ビタミンＣ）、アナトー色素\r\n\r\n・アレルギー\r\n乳、小麦、大豆、りんご\r\n\r\n・説明\r\nチョコでくるんだザクザククッキー\r\nアプリコット＆リンゴのゼリーチップがぎっしり♪', '8.jpg'),
(11, 'マリー', 206, 100, 10, '\r\n・内容量：24枚（3枚パック×8袋）\r\n\r\n・栄養成分[ 1枚（標準5.8g）当り ]\r\n熱量：25kcal、たんぱく質：0.4g、脂質：0.6g、炭水化物：4.6g、ナトリウム：19mg\r\n\r\n・原材料\r\n小麦粉、砂糖、牛乳、とうもろこしでん粉、ショートニング、バターオイル、マーガリン、全粉乳、植物油脂、ぶどう糖果糖液糖、食塩、たんぱく質濃縮ホエイパウダー、膨脹剤、香料、乳化剤（大豆由来）\r\n\r\n・アレルギー\r\n乳、小麦、大豆\r\n\r\n・説明\r\nやさしく広がるミルクの味わいです。', '9.jpg'),
(12, 'チョイス', 173, 100, 10, '\r\n・内容量：16枚（2枚パック×8袋）\r\n\r\n・栄養成分[ 1枚（標準9.2g）当り ]\r\n熱量：48kcal、たんぱく質：0.6g、脂質：2.3g、炭水化物：6.1g、ナトリウム：17mg\r\n\r\n・原材料\r\n小麦粉、砂糖、ショートニング、卵白、バターオイル、バター、植物油脂、チーズパウダー、たんぱく質濃縮ホエイパウダー、食塩、膨脹剤、香料、乳化剤(大豆由来)\r\n\r\n・アレルギー\r\n卵、乳、小麦、大豆\r\n\r\n・説明\r\nふわっと香ばしいバターの香り。\r\n', '10.jpg'),
(13, 'ムーンライト', 178, 100, 10, '\r\n・内容量：16枚（2枚パック×8袋）\r\n\r\n・栄養成分[ 1枚（標準8.5g）当り ]\r\n熱量：44kcal、たんぱく質：0.5g、脂質：2.3g、炭水化物：5.4g、ナトリウム：16mg\r\n\r\n・原材料\r\n小麦粉、砂糖、ショートニング、鶏卵、バターオイル、植物油脂、マーガリン、卵黄、食塩、乳化剤(大豆由来)、香料、膨脹剤、カロテン色素\r\n\r\n・アレルギー\r\n卵、乳、小麦、大豆\r\n・説明\r\nふんわりまろやかな卵の味わいです。', '11.jpg'),
(14, 'チョコチップクッキー', 178, 100, 10, '\r\n・内容量：14枚（2枚パック×7袋）\r\n\r\n・栄養成分[ 1枚（標準9.5g）当り ]\r\n熱量：50kcal、たんぱく質：0.6g、脂質：2.6g、炭水化物：6.1g、ナトリウム：15mg\r\n\r\n・原材料\r\n小麦粉、チョコレートチップ、ショートニング、砂糖、オートミール、ぶどう糖果糖液糖、ホエイパウダー（乳製品）、ココアパウダー、黒みつ、食塩、膨脹剤、乳化剤（大豆由来）、香料\r\n\r\n・アレルギー\r\n乳、小麦、大豆\r\n\r\n・説明\r\nたっぷりチョコチップ♪', '12.jpg'),
(15, 'アーモンドクッキー', 238, 100, 10, '\r\n・内容量：14枚（2枚パック×7袋）\r\n\r\n・栄養成分[ 1枚（標準8.6g）当り ]\r\n熱量：46kcal、たんぱく質：0.6g、脂質：2.6g、炭水化物：5.1g、ナトリウム：21mg\r\n\r\n・原材料\r\n小麦粉、砂糖、ローストアーモンド、マーガリン（乳成分を含む）、ショートニング、アーモンドペースト、食塩、クリーム加工品、膨脹剤、乳化剤（大豆由来）、酸化防止剤（ビタミンＥ）、香料\r\n\r\n・アレルギー\r\n乳、小麦、大豆\r\n\r\n・説明\r\n粗挽きローストアーモンド\r\nアーモンドペーストを練り込んだクッキー', '13.jpg'),
(16, '森永の煎餅<キャラメル味>', 148, 100, 11, '\r\n・内容量：50g\r\n\r\n・栄養成分[ 1袋（50g）当り ]\r\n熱量：247kcal、たんぱく質：2.6g、脂質：11.3g、炭水化物：33.7g、ナトリウム：245mg\r\n\r\n・原材料\r\nキャラメルクリーム（砂糖、植物油脂、乳糖、全粉乳、キャラメル粉末、食塩）、米（国産）、小麦粉、砂糖、米パフパウダー、しょうゆ、ぶどう糖、食塩、植物油脂、乳化剤（大豆由来）、トレハロース、香料、膨脹剤、酸化防止剤（ビタミンＥ）\r\n\r\n・その他\r\n食塩相当量：0.27g\r\n\r\n・アレルギー\r\n乳、小麦、大豆\r\n\r\n・説明\r\n新食感！甘じょっぱさがくせになる\r\n国産米を100％使用した塩せんべいで、\r\n森永製菓独自のキャラメルクリームを包みました。', '14.jpg'),
(17, 'おっとっと<うすしお味>', 54, 100, 11, '\r\n・内容量：19g\r\n\r\n・栄養成分[ 1袋（19g）当り ]\r\n熱量：84kcal、たんぱく質：1.1g、脂質：2.4g、炭水化物：14.4g、ナトリウム：107mg、カルシウム：52mg\r\n\r\n・原材料\r\n乾燥じゃがいも、ショートニング、小麦粉、植物油脂、ホエイパウダー（乳製品）、砂糖、とうもろこしでん粉、シーズニングパウダー（食塩、乳糖、チキンパウダー、オニオンエキスパウダー、酵母エキスパウダー（大豆を含む）、麦芽糖、香辛料）、食塩、たんぱく加水分解物、加工デンプン、貝Ｃａ、調味料（アミノ酸等）、膨脹剤、乳化剤、香料（キウイフルーツ由来）、酸化防止剤（ビタミンＥ、ビタミンＣ）、カロテン色素\r\n\r\n・その他\r\n食塩相当量：0.27g\r\n\r\n・アレルギー\r\n乳、小麦、キウイフルーツ、大豆、鶏肉\r\n\r\n・説明\r\nいつものなかま10種類 ひみつのなかま7種類。\r\nダイオウイカに会えるかも？\r\n※18種類の形が全て入っているとは限りません。', '15.jpg'),
(18, 'ファッジ', 132, 100, 12, '\r\n・内容量：38g\r\n\r\n・栄養成分[ 1袋（38g）当り ]\r\n熱量：165kcal、たんぱく質：0.9g、脂質：4.3g、炭水化物：30.7g、ナトリウム：81mg\r\n\r\n・原材料\r\n砂糖、乳製品、水あめ、クリーミングパウダー（乳製品、乳糖）、植物油脂、食塩、デキストリン、ステアリン酸カルシウム、乳化剤（大豆由来）、香料、酸化防止剤（ビタミンＣ、ビタミンＥ）\r\n\r\n・アレルギー\r\n乳、大豆\r\n\r\n・説明\r\n英国で愛されている伝統的なお菓子「ファッジ」が、\r\nキャラメルの森永から生まれました。\r\nほろほろ口どける食感はそのままに、\r\n後味がすっきりしてより食べやすくなりました。', '16.jpg'),
(19, 'プレミアムミルクキャラメル', 520, 100, 12, '\r\n・内容量：12粒\r\n\r\n・栄養成分[ 1粒（標準4.9g）当り ]\r\n熱量：21kcal、たんぱく質：0.24g、脂質：0.49g、炭水化物：3.8g、ナトリウム：6.4mg\r\n\r\n・原材料\r\n水あめ、加糖練乳、砂糖、生クリーム、加糖脱脂練乳、全粉乳、バター、カラメルソース、小麦たんぱく、モルトエキス、黒みつ、食塩、ソルビトール、乳化剤（大豆由来）、香料\r\n\r\n・アレルギー\r\n乳、小麦、大豆\r\n\r\n・説明\r\n森永謹製\r\nフランス産ミルクを使った濃厚な味わいのキャラメルです。\r\nフランス産乳原料7％使用（全乳原料中の割合）', '17.jpg'),
(20, 'ハイチュウ<ゴールデンパイ>', 48, 100, 13, '\r\n・内容量：12粒\r\n\r\n・栄養成分[ 1粒（標準4.8g）当り ]\r\n熱量：19kcal 、たんぱく質 ：0.07g 、脂質：0.36g 、炭水化物：3.8g 、ナトリウム：0mg\r\n\r\n・原材料\r\n水あめ、砂糖、植物油脂、ゼラチン、濃縮パインアップル果汁（ゴールデンパイン）、酸味料、香料、乳化剤、ベニバナ黄色素\r\n\r\n・アレルギー\r\nゼラチン\r\n\r\n・説明\r\n果実感あふれるジューシーフレッシュパイン。', '18.jpg'),
(21, 'ハイチュウ<完熟メロン>', 108, 100, 13, '\r\n・内容量：12粒\r\n\r\n・栄養成分[ 1粒（標準4.6g）当り ]\r\n熱量：19kcal 、たんぱく質 ：0.07g 、脂質：0.36g 、炭水化物：3.8g 、ナトリウム：0mg\r\n\r\n・原材料\r\n水あめ、砂糖、植物油脂、ゼラチン、はちみつ、濃縮ヨーグルト、濃縮メロン果汁、香料、酸味料、乳化剤、ベニバナ黄色素、クチナシ青色素\r\n\r\n・アレルギー\r\n乳、ゼラチン\r\n\r\n・説明\r\n完熟したメロンの味わいをイメージしてつくりました\r\n期間限定のハイチュウ＜完熟メロン＞です。', '19.jpg'),
(22, 'ドトールフローズンカフェ・オ・レ', 149, 100, 14, '\r\n・内容量：165ml\r\n\r\n・栄養成分[ 1カップ当り ]\r\n熱量：166kcal、たんぱく質：2.6g、脂質：4.6g、炭水化物：28.5g、ナトリウム：38mg\r\n\r\n・原材料\r\n砂糖、乳製品、コーヒー、水あめ、植物油、異性化液糖、デキストリン、卵黄、安定剤（ゼラチン、増粘多糖類）、乳化剤、香料\r\n\r\n・アレルギー\r\n卵、乳、ゼラチン\r\n\r\n・説明\r\n毎年大好評の「ドトールフローズンカフェ・オ・レ」を\r\n今年も期間限定で発売します。\r\n\r\n※ドトールコーヒーショップでは販売しておりません。', '20.jpg'),
(23, 'アイスボックス<グレープフルーツ>', 149, 100, 14, '\r\n・内容量：135ml\r\n\r\n・栄養成分[ 1カップ 当り ]\r\n熱量：13kcal、たんぱく質：0g、脂質：0g、炭水化物：3.3g、ナトリウム：16mg\r\n\r\n・原材料\r\nグレープフルーツ果汁、異性化液糖、香料、酸味料、甘味料(アスパルテーム・Ｌ-フェニルアラニン化合物、スクラロース、アセスルファムＫ)、ポリリン酸Na、カロテン色素\r\n\r\n・アレルギー\r\n無し\r\n\r\n・説明\r\nキーンと爽快！クラッシュ氷！', '21.jpg'),
(24, 'チョコビスコッティ', 148, 100, 15, '\r\n・内容量；92ｍｌ\r\n\r\n・栄養成分[ 1個当り ]\r\n熱量：311kcal、たんぱく質：4.6g、脂質：18.9g、炭水化物：30.7g、ナトリウム：93mg\r\n\r\n・原材料\r\n乳製品、チョコレートコーチング、ビスケット（小麦を含む）、砂糖、水あめ、卵黄、デキストリン、乳化剤（大豆由来）、香料、膨脹剤、安定剤（増粘多糖類）\r\n\r\n・アレルギー\r\n卵、乳、小麦\r\n\r\n・説明\r\nくちどけなめらかセミスイートチョコ\r\nコクのあるこだわりバニラアイスクリーム\r\n香ばしい小麦胚芽ビスケット', '22.jpg'),
(25, 'ニコサンド', 148, 100, 15, '\r\n・内容量：40ml×2個\r\n\r\n・栄養成分[ 1個当り ]\r\n熱量：107kcal、たんぱく質：1.4g、脂質：4.5g、炭水化物：15.1g、ナトリウム：63mg\r\n\r\n・原材料\r\nクッキー（小麦を含む）、砂糖、乳製品、水あめ、植物油、デキストリン、膨脹剤、乳化剤（大豆由来）、安定剤（増粘多糖類）、香料、カロテン色素\r\n\r\n・アレルギー\r\n乳、小麦、大豆\r\n\r\n・説明\r\nソルティーバタークッキーでミルクアイスをサンドしました。', '23.jpg'),
(26, '板チョコアイス<Wクッキー>', 148, 100, 16, '\r\n・内容量：72ｍｌ\r\n\r\n・栄養成分[ 1個当り ]\r\n熱量：301kcal、たんぱく質：2.9g、脂質：20.5g、炭水化物：26.3g、ナトリウム：45mg\r\n\r\n・原材料\r\nチョコレートコーチング、クッキークランチ（小麦を含む）、砂糖、乳製品、水あめ、植物油、デキストリン、乳化剤（大豆由来）、香料、安定剤（増粘多糖類）、膨脹剤、カロテン色素\r\n\r\n・アレルギー\r\n乳、小麦、大豆\r\n\r\n・説明\r\nクッキーがWに入って美味しさW！\r\n新しくなったザクザク食感をお楽しみください。', '24.jpg'),
(27, 'ホットケーキイチゴのデザートアイス', 148, 100, 16, '\r\n・内容量：70ｍｌ\r\n\r\n・栄養成分[ 1個当り ]\r\n熱量：196kcal、たんぱく質：2.9g、脂質：7.6g、炭水化物：28.9g、ナトリウム：104mg\r\n\r\n・原材料\r\nホットケーキシート（小麦粉、鶏卵、砂糖、還元水あめ、植物油、乳製品、水あめ、食塩、グルコマンナン、乳清たんぱく）、いちごソース、乳製品、砂糖、卵黄、デキストリン、水あめ、安定剤（増粘多糖類、ゼラチン）、乳化剤（大豆由来）、ｐＨ調整剤、膨脹剤、酸味料、香料、着色料（紅麹、アナトー、ウコン）、乳酸Ｃａ、加工デンプン、脂肪酸、セルロース\r\n\r\n・アレルギー\r\n卵、乳、小麦、大豆、ゼラチン\r\n\r\n・説明\r\n果肉入り苺ソースをかけたミルクアイスを\r\nしっとりふんわりホットケーキで包んだデザートアイスです。', '25.jpg'),
(28, 'パキシエル', 340, 100, 17, '\r\n・内容量：40ｍｌ×7本\r\n\r\n・栄養成分[ 1本当り ]\r\n熱量：133kcal、たんぱく質：1.1g、脂質：10.1g、炭水化物：9.4g、ナトリウム：12mg\r\n\r\n・原材料\r\nチョコレートコーチング、砂糖、乳製品、植物油（ピーナッツを含む）、ココアパウダー、異性化液糖、デキストリン、乳化剤（大豆由来）、安定剤（増粘多糖類）、香料\r\n\r\n・アレルギー\r\n乳、ピーナッツ、大豆\r\n\r\n・説明\r\nチョコレート好きに愛される粒ショコラのように\r\nパキッとしたチョコレートの殻（シェル）のなかに\r\nなめらかなチョコレートアイスをとじこめた\r\nチョコレート好きに贈るチョコレートアイスバーです。', '26.jpg'),
(29, 'パリパリバー<バニラ>', 380, 100, 17, '\r\n・内容量：48ｍｌ×8本\r\n\r\n・栄養成分[ 1本当り ]\r\n熱量：91kcal、たんぱく質：1.2g、脂質：5.7ml、炭水化物：8.6g、ナトリウム：19mg\r\n\r\n・原材料\r\nチョコレートコーチング、砂糖、乳製品、植物油、水あめ、デキストリン、食塩、乳化剤（大豆由来）、香料、安定剤（増粘多糖類、ゼラチン）、カロテン色素\r\n\r\n・アレルギー\r\n乳、大豆、ゼラチン\r\n\r\n・説明\r\nパリパリハッピー\r\nパリパリチョコ＆ミルクバニラの重なるおいしさ♪', '27.jpg'),
(30, 'ウイダーinゼリー　プロテイン', 200, 100, 18, '\r\n・内容量：180g\r\n\r\n・栄養成分[ 1袋（180g）当り ]\r\n熱量：90kcal、たんぱく質：5.0g、脂質：0g、炭水化物：17.5g、ナトリウム：79mg、カルシウム：60mg、ビタミンB1：0.10～0.32mg、ビタミンB2：0.11～0.27mg、ビタミンB6：0.33～0.76mg、ビタミンB12：0.67～1.9μg、ナイアシン：1.1～2.1mg、葉酸：67～800μg、パントテン酸：0.55～3.7mg\r\n･････････････････････････････\r\nホエイペプチド：5,000mg、クエン酸：1,000mg\r\n\r\n※上記のたんぱく質5.0gは、ホエイペプチドを含んだ量です。\r\n※上記の炭水化物17.5gは、クエン酸1,000mgを含んだ量です。\r\n\r\n・原材料\r\n果糖ぶどう糖液糖、砂糖、ホエイペプチド（ホエイたんぱく質分解物）、安定剤（大豆多糖類）、クエン酸、ゲル化剤（増粘多糖類）、乳酸Ｃａ、香料、乳化剤、塩化Ｋ、甘味料（スクラロース）、酵素処理ルチン、パントテン酸Ｃａ、葉酸、Ｖ.Ｂ6、Ｖ.Ｂ1、Ｖ.Ｂ2、Ｖ.Ｂ12、（原材料の一部に乳成分を含む）\r\n\r\n・アレルギー\r\n乳、大豆\r\n\r\n・説明\r\n飲むコンディショニング\r\nウイダーinゼリー プロテイン ヨーグルト味 ＥＭＲ配合\r\nたんぱく質の働きを強めるＥＭＲ※14mg配合 ※「EMR=酵素処理ルチン」\r\nホエイペプチド5,000mg配合', '28.jpg'),
(31, 'ウイダーinゼリー　エネルギー', 173, 100, 18, '\r\n・内容量：180g\r\n\r\n・栄養成分[ 1袋（180g）当り ]\r\n熱量：180kcal、たんぱく質：0g、脂質：0g、炭水化物：45g、ナトリウム：43mg、ビタミンA：45～120μg、ビタミンB1：0.09～0.22mg、ビタミンB2：0.11～0.21mg、ビタミンB6：0.10～0.20mg、ビタミンB12：0.20～0.67μg、ナイアシン：1.0～1.9mg、ビタミンC：80～190mg、ビタミンD：0.42～1.7μg、ビタミンE：0.74～1.2mg、葉酸：20～80μg、パントテン酸：0.46～2.1mg\r\n\r\n・原材料\r\nマルトデキストリン、果糖ぶどう糖液糖、マスカット果汁、ゲル化剤（増粘多糖類）、乳酸Ｃａ、クエン酸、Ｖ.Ｃ、クエン酸Ｎａ、香料、塩化Ｋ、乳化剤、パントテン酸Ｃａ、ナイアシン、Ｖ.Ｅ、Ｖ.Ｂ1、Ｖ.Ｂ2、Ｖ.Ｂ6、Ｖ.Ａ、葉酸、Ｖ.Ｄ、Ｖ.Ｂ12\r\n\r\n・アレルギー\r\n無し\r\n\r\n・説明\r\n飲むコンディショニング\r\nウイダーinゼリー エネルギー マスカット味\r\nすばやいエネルギー補給に（おにぎりおよそ1個分）\r\n1日分以上のビタミンCを配合※ 180kcal\r\n※「栄養素等表示基準値」を目安にしています。', '29.jpg'),
(32, '森永ミルクチョコレート', 118, 100, 9, '\r\n・内容量：55g\r\n\r\n・栄養成分[ 1枚（55g）当り ]\r\n熱量：313kcal、たんぱく質：4.0g、脂質：19.8g、炭水化物：29.7g、ナトリウム：31mg\r\n\r\n・原材料\r\n砂糖、カカオマス、植物油脂、全粉乳、ココアバター、脱脂粉乳、バターオイル、乳化剤(大豆由来)、香料\r\n\r\n・アレルギー\r\n乳、大豆\r\n\r\n・説明\r\n森永の代表的な板チョコ', '30.jpg'),
(33, 'カレ・ド・ショコラ<フレンチミルク>', 442, 100, 9, '\r\n・内容量：107g（21枚）\r\n\r\n・栄養成分[ 1枚（標準5.1g）当り ]\r\n熱量：29kcal、たんぱく質：0.4g、脂質：1.8g、炭水化物：2.7g、ナトリウム：3.9mg\r\n\r\n・原材料\r\n砂糖、ココアバター、カカオマス、脱脂粉乳、全粉乳（フランス産100％）、バターオイル、乳化剤(大豆由来）、香料\r\n\r\n・アレルギー\r\n乳、大豆\r\n\r\n・説明\r\nカレ・ド・ショコラ＜フレンチミルク＞は\r\nフランス産ミルクの豊潤な味わいと\r\nしっかりとしたカカオ感の両方を追求した\r\n本格ミルクチョコレートです。\r\n※フランス産乳原料29％使用(全乳原料中の割合)', '31.jpg'),
(34, 'カレ・ド・ショコラ<ベネズエラビター>', 442, 100, 9, '\r\n・内容量：107g（21枚）\r\n\r\n・栄養成分[ 1枚（標準5.1g）当り ]\r\n熱量：29kcal、たんぱく質：0.24g、脂質：1.9g、炭水化物：2.8g、ナトリウム：0.12mg\r\n\r\n・原材料\r\nカカオマス（ベネズエラ産カカオ豆30％）、砂糖、ココアバター、乳化剤（大豆由来）、香料、（原材料の一部に乳成分を含む）\r\n\r\n・アレルギー\r\n乳、大豆\r\n\r\n・説明\r\nカレ･ド･ショコラ＜ベネズエラビター＞は\r\nカカオ分55％の本格ビターチョコレート。\r\nベネズエラ産カカオ豆の深い香りと\r\n苦みや酸味をおさえたマイルドな味わいが特長です。', '32.jpg'),
(35, 'カレ・ド・ショコラ<カカオ70>', 442, 100, 9, '\r\n・内容量：102g（21枚）\r\n\r\n・栄養成分[ 1枚（標準4.9g）当り ]\r\n熱量：29kcal、たんぱく質：0.4g、脂質：2.0g、炭水化物：2.3g、ナトリウム：0.12mg\r\n\r\n・原材料\r\nカカオマス、砂糖、ココアバター、ココアパウダー、乳化剤（大豆由来）、香料、（原材料の一部に乳成分を含む）\r\n\r\n・アレルギー\r\n乳、大豆\r\n\r\n・説明\r\nカレ･ド･ショコラ＜カカオ70＞は、\r\nカカオ感たっぷりの美味しさにこだわりました。\r\n甘さをおさえ後味スッキリとした\r\nカカオ分70％の本格ビターチョコレートをお楽しみください。', '33.jpg'),
(36, 'カレ・ド・ショコラ<マダガスカルホワイト>', 442, 100, 9, '\r\n・内容量：107g（21枚）\r\n\r\n・栄養成分[ 1枚（標準5.1g）当り ]\r\n熱量：30kcal、たんぱく質：0.4g、脂質：2.0g、炭水化物：2.6g、ナトリウム：4.7mg\r\n\r\n・原材料\r\n砂糖、全粉乳、ココアバター、植物油脂、脱脂粉乳、バニラビーンズ(マダガスカル産100％）、乳化剤(大豆由来)、香料\r\n\r\n・アレルギー\r\n乳、大豆\r\n\r\n・説明\r\nマダガスカル産バニラのふくよかな香りに\r\nミルクの深いコクがマッチしたチョコレートです。\r\nコーヒーや緑茶のお供にもおすすめです。', '34.jpg'),
(37, 'チーズサブレ', 318, 100, 10, '\r\n・内容量：12枚（1枚パック×12袋）\r\n\r\n・栄養成分[ 1袋（標準7.3g）当り ]\r\n熱量：39kcal、たんぱく質：0.5g、脂質：2.3g、炭水化物：4.1g、ナトリウム：33mg\r\n\r\n・原材料\r\n小麦粉、砂糖、マーガリン、ショートニング、チーズパウダー、チーズフード、アーモンドパウダー、食塩、膨脹剤、乳化剤（大豆由来）、香料、酸化防止剤（ビタミンＥ）\r\n\r\n・アレルギー\r\n乳、小麦、大豆\r\n\r\n・説明\r\nチェダーチーズのコクと旨み。', '35.jpg'),
(38, 'エンゼルパイ', 316, 100, 10, '\r\n・内容量：2個\r\n\r\n・栄養成分[ 1個（標準32g）当り ]\r\n熱量：139kcal、たんぱく質：1.7g、脂質：5.1g、炭水化物：21.5g、ナトリウム：39mg\r\n\r\n・原材料\r\n小麦粉、水あめ、乳糖、砂糖、植物油脂、ショートニング、麦芽糖、カカオマス、オリゴ糖、全粉乳、脱脂粉乳、ぶどう糖、鶏卵、ココアパウダー、ゼラチン、食塩、黒みつ、ソルビトール、酒精、乳化剤（大豆由来）、膨脹剤、香料\r\n\r\n・アレルギー\r\n卵、乳、小麦、大豆、ゼラチン\r\n\r\n・説明\r\n天使のスイーツ♪エンゼルパイ\r\nもっちりふにふに チョコレートパイ\r\n中はマシュマロだよ♪', '36.jpg'),
(39, 'おっとっと<たこ焼きマヨ味>', 141, 100, 11, '\r\n・内容量：52g（26g×2袋）\r\n\r\n・栄養成分[ 1袋（26g）当り ]\r\n熱量：114kcal、たんぱく質：1.5g、脂質：3.1g、炭水化物：19.9g、ナトリウム：187mg、カルシウム：70mg\r\n\r\n・原材料\r\n乾燥じゃがいも、小麦粉、ショートニング、砂糖、ホエイパウダー（乳製品）、植物油脂、シーズニングパウダー（砂糖、食塩、ソースパウダー（大豆・豚肉・りんごを含む）、たんぱく加水分解物、香辛料、かつおぶし）、とうもろこしでん粉、食塩、たんぱく加水分解物、加工デンプン、貝Ｃａ、膨脹剤、乳化剤、調味料（アミノ酸等）、香料（キウイフルーツ由来）、カラメル色素、酸味料、カロテン色素、甘味料（ステビア）、酸化防止剤（ビタミンＥ、ビタミンＣ）\r\n\r\n・その他\r\n食塩相当量：0.48g\r\n\r\n・アレルギー\r\n乳、小麦、キウイフルーツ、大豆、豚肉、りんご\r\n\r\n・説明\r\nいつものなかま10種類 ひみつのなかま7種類。\r\n大ダコに会えるかも？\r\n※18種類の形が全て入っているとは限りません。', '37.jpg'),
(40, 'おっとっと<うすしお味>（カップ）', 121, 100, 11, '\r\n・内容量：30g\r\n\r\n・栄養成分[ 1カップ（30g）当り ]\r\n熱量：132kcal、たんぱく質：1.8g、脂質：3.8g、炭水化物：22.7g、ナトリウム：169mg、カルシウム：82mg\r\n\r\n・原材料\r\n乾燥じゃがいも、ショートニング、小麦粉、植物油脂、ホエイパウダー（乳製品）、砂糖、とうもろこしでん粉、シーズニングパウダー（食塩、乳糖、チキンパウダー、オニオンエキスパウダー、酵母エキスパウダー（大豆を含む）、麦芽糖、香辛料）、食塩、たんぱく加水分解物、加工デンプン、貝Ｃａ、調味料（アミノ酸等）、膨脹剤、乳化剤、香料（キウイフルーツ由来）、酸化防止剤（ビタミンＥ、ビタミンＣ）、カロテン色素\r\n\r\n・その他\r\n食塩相当量：0.43g\r\n\r\n・アレルギー\r\n乳、小麦、キウイフルーツ、大豆、鶏肉\r\n\r\n・説明\r\nいつものなかま10種類 ひみつのなかま7種類。\r\nダイオウイカに会えるかも？\r\n※18種類の形が全て入っているとは限りません。', '38.jpg'),
(41, 'おっとっと<うすしお味>（箱）', 132, 100, 11, '\r\n・内容量：54g（27g×2袋）\r\n\r\n・栄養成分[ 1袋（27g）当り ]\r\n熱量：119kcal、たんぱく質：1.6g、脂質：3.4g、炭水化物：20.4g、ナトリウム：152mg、カルシウム：74mg\r\n\r\n・原材料\r\n乾燥じゃがいも、ショートニング、小麦粉、植物油脂、ホエイパウダー（乳製品）、砂糖、とうもろこしでん粉、シーズニングパウダー（食塩、乳糖、チキンパウダー、オニオンエキスパウダー、酵母エキスパウダー（大豆を含む）、麦芽糖、香辛料）、食塩、たんぱく加水分解物、加工デンプン、貝Ｃａ、調味料（アミノ酸等）、膨脹剤、乳化剤、香料（キウイフルーツ由来）、酸化防止剤（ビタミンＥ、ビタミンＣ）、カロテン色素\r\n\r\n・その他\r\n食塩相当量：0.39g\r\n\r\n・アレルギー\r\n乳、小麦、キウイフルーツ、大豆、鶏肉\r\n\r\n・説明\r\nいつものなかま10種類 ひみつのなかま7種類。\r\nダイオウイカに会えるかも？\r\n※18種類の形が全て入っているとは限りません。', '39.jpg'),
(42, 'ポテロング<しお味>', 121, 100, 11, '\r\n・内容量：45g\r\n\r\n・栄養成分[ 1カップ（45g）当り ]\r\n熱量：213kcal、たんぱく質：3.5g、脂質：8.5g、炭水化物：30.5g、ナトリウム：262mg\r\n\r\n・原材料\r\n乾燥じゃがいも、植物油脂、米粉、小麦粉、とうもろこし、コーンフレーク、食塩、しょうゆ、調味パウダー（豚肉を含む）、乳化剤、膨脹剤、調味料（アミノ酸等）、香料\r\n\r\n・その他\r\n食塩相当量：0.7g\r\n\r\n・アレルギー\r\n小麦、大豆、豚肉\r\n\r\n・説明\r\nサックサク食感と塩味が効いたポテトの旨味がくせになる！\r\nなが?いノンフライスナック！！\r\nうまいニャ?サックサクだワン', '40.jpg'),
(43, 'ポテロング<ホタテバターしょうゆ味>', 121, 100, 11, '\r\n・内容量：45g\r\n\r\n・栄養成分[ 1カップ（45g）当り ]\r\n熱量：212kcal、たんぱく質：3.5g、脂質：8.3g、炭水化物：30.7g、ナトリウム：251mg\r\n\r\n・原材料\r\n乾燥じゃがいも、植物油脂、小麦粉、米粉、とうもろこし、シーズニングパウダー（しょうゆ粉末、砂糖、食塩、香辛料、たんぱく加水分解物、デキストリン、乳等を主要原料とする食品、ホタテエキスパウダー）、コーンフレーク、しょうゆ、調味パウダー、加工デンプン、調味料（アミノ酸等）、乳化剤、香料、膨脹剤、（原材料の一部に乳成分、豚肉、ゼラチンを含む）\r\n\r\n・その他\r\n食塩相当量：0.6g\r\n\r\n・アレルギー\r\n乳、小麦、大豆、豚肉、ゼラチン\r\n\r\n・説明\r\nホタテの旨味と、まろやかなバター醤油の風味が後を引く！\r\nくせになる味わいのポテロング！！\r\nうまいよ～', '41.jpg'),
(44, 'マンゴーキャラメル', 123, 100, 12, '\r\n・内容量：12粒\r\n\r\n・栄養成分[ 1粒（標準4.9g）当り ]\r\n熱量：20kcal、たんぱく質：0.19g、脂質：0.38g、炭水化物：3.9g、ナトリウム：6mg\r\n\r\n・原材料\r\n水あめ、加糖練乳、砂糖、加糖脱脂練乳、植物油脂、小麦たんぱく、バター、濃縮マンゴー果汁、食塩、ソルビトール、酸味料、乳化剤（大豆由来）、香料、ベニバナ黄色素、パプリカ色素、（原材料の一部にももを含む）\r\n\r\n・アレルギー\r\n乳、小麦、大豆、もも\r\n\r\n・説明\r\n森永ミルクキャラメルシリーズから「マンゴーキャラメル」が新発売。\r\nアルフォンソマンゴーの濃厚な味わいをイメージしたキャラメルです。', '42.jpg'),
(45, '木いちごキャラメル', 161, 100, 12, '\r\n・内容量：12粒\r\n\r\n・栄養成分[ 1粒（標準4.9g）当り ]\r\n熱量：20kcal、たんぱく質：0.21g、脂質：0.38g、炭水化物：3.9g、ナトリウム：6mg\r\n\r\n・原材料\r\n水あめ、加糖練乳、砂糖、加糖脱脂練乳、植物油脂、小麦たんぱく、バター、濃縮いちご果汁、食塩、ソルビトール、酸味料、香料、乳化剤（大豆由来）、アカビート色素\r\n\r\n・アレルギー\r\n乳、小麦、大豆\r\n\r\n・説明\r\n甘ずっぱい木いちごの味わいをイメージしたキャラメルです。', '43.jpg'),
(46, 'あずきキャラメル', 123, 100, 12, '\r\n・内容量：12粒\r\n\r\n・栄養成分[ 1粒（標準4.9g）当り ]\r\n熱量：20kcal、たんぱく質：0.23g、脂質：0.48g、炭水化物：3.8g、ナトリウム：6.0mg\r\n\r\n・原材料\r\n水あめ、加糖練乳、砂糖、加糖脱脂練乳、植物油脂、さらしあん、小麦たんぱく、バター、食塩、黒豆エキス（大豆を含む）、ソルビトール、香料、乳化剤、アカビート色素\r\n\r\n・アレルギー\r\n乳、小麦、大豆\r\n\r\n・説明\r\n森永ミルクキャラメルシリーズの「あずきキャラメル」。', '44.jpg'),
(47, 'ミルクキャラメル', 123, 100, 12, '\r\n・内容量：12粒\r\n\r\n・栄養成分[ 1粒（標準4.9g）当り ]\r\n熱量：21kcal、たんぱく質：0.19g、脂質：0.51g、炭水化物：3.8g、ナトリウム：5.2mg、カルシウム：5.1mg、鉄：0.044mg、ビタミンＡ：18μg、ビタミンＢ1：0.0098mg、ビタミンＢ2：0.010mg、ナイアシン：0.20mg、ビタミンＤ：0.37μg\r\n\r\n・原材料\r\n水あめ、加糖練乳、砂糖、加糖脱脂練乳、植物油脂、小麦たんぱく、バター、モルトエキス、黒みつ、食塩、ソルビトール、乳化剤（大豆由来）、香料\r\n\r\n・アレルギー\r\n乳、小麦、大豆\r\n\r\n・説明\r\n森永のミルクキャラメル。', '45.jpg'),
